import React, { useState, useRef, useEffect } from "react";
import { IconButton, Slider, Box, Typography } from "@mui/material";
import { PlayArrow, Pause, VolumeUp, MusicNote } from "@mui/icons-material";

export default function MusicPlayer() {
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(0.5);
  const [isReady, setIsReady] = useState(false);
  const audioRef = useRef(null);

  // Configuração inicial do áudio
  useEffect(() => {
    const audio = new Audio("publicmusicaCinnamon-Girl.mp3");
    audio.volume = volume;
    audio.loop = true;

    audio.addEventListener("canplaythrough", () => setIsReady(true));
    audio.addEventListener("error", (e) => {
      console.error("Erro no áudio:", e.target.error);
      setIsReady(false);
    });

    audioRef.current = audio;

    return () => {
      audio.pause();
      audio.removeEventListener("canplaythrough", () => {});
      audio.removeEventListener("error", () => {});
    };
  }, []);

  const togglePlay = () => {
    if (!isReady) return;

    if (isPlaying) {
      audioRef.current.pause();
    } else {
      audioRef.current
        .play()
        .catch((error) => console.error("Erro ao reproduzir:", error));
    }
    setIsPlaying(!isPlaying);
  };

  const handleVolumeChange = (event, newValue) => {
    setVolume(newValue);
    if (audioRef.current) {
      audioRef.current.volume = newValue;
    }
  };

  return (
    <Box
      sx={{
        position: "fixed",
        bottom: 16,
        right: 16,
        bgcolor: "rgba(0,0,0,0.8)",
        borderRadius: "50px",
        p: 1,
        display: "flex",
        alignItems: "center",
        zIndex: 9999,
        backdropFilter: "blur(5px)",
      }}
    >
      {!isReady && (
        <Typography variant="caption" sx={{ color: "error.main", mr: 1 }}>
          Música não carregada
        </Typography>
      )}

      <IconButton onClick={togglePlay} color="primary" disabled={!isReady}>
        {isPlaying ? <Pause /> : <PlayArrow />}
      </IconButton>

      <MusicNote sx={{ ml: 1, color: "primary.main" }} />
      <Slider
        value={volume}
        onChange={handleVolumeChange}
        min={0}
        max={1}
        step={0.01}
        sx={{ width: "100px", ml: 1, color: "primary.main" }}
        disabled={!isReady}
      />
    </Box>
  );
}
