import React from "react";
import {
  Box,
  Typography,
  IconButton,
  Card,
  CardContent,
  Avatar,
  Divider,
  Button,
} from "@mui/material";
import FavoriteIcon from "@mui/icons-material/Favorite";
import LockIcon from "@mui/icons-material/Lock";
import CelebrationIcon from "@mui/icons-material/Celebration";

const BodyMap = ({ bodyParts, unlockedParts, score, totalQuestions }) => {
  const [selectedPart, setSelectedPart] = React.useState(null);

  const handlePartClick = (partKey) => {
    if (unlockedParts.includes(partKey)) {
      setSelectedPart(bodyParts[partKey]);
    }
  };

  return (
    <Box sx={{ textAlign: "center" }}>
      <Typography variant="h3" component="h2" gutterBottom>
        {score === totalQuestions ? (
          <>ParabÃ©ns! ðŸŽ‰ VocÃª acertou tudo!</>
        ) : (
          <>
            VocÃª acertou {score} de {totalQuestions}!
          </>
        )}
      </Typography>

      <Typography variant="body1" sx={{ mb: 4 }}>
        {score === totalQuestions
          ? "VocÃª conhece nosso amor perfeitamente!"
          : "Clique nas partes desbloqueadas para ver nossas memÃ³rias especiais!"}
      </Typography>

      <Box
        sx={{
          position: "relative",
          width: "100%",
          height: { xs: "400px", md: "500px" },
          my: 4,
          backgroundImage: "url(/body-outline.png)",
          backgroundSize: "contain",
          backgroundRepeat: "no-repeat",
          backgroundPosition: "center",
        }}
      >
        {Object.entries(bodyParts).map(([key, part]) => (
          <IconButton
            key={key}
            sx={{
              position: "absolute",
              ...part.position,
              color: unlockedParts.includes(key)
                ? "primary.main"
                : "text.disabled",
              fontSize: "3rem",
              "& .MuiSvgIcon-root": {
                fontSize: "inherit",
              },
              filter: unlockedParts.includes(key)
                ? "drop-shadow(0 0 10px rgba(138, 43, 226, 0.7))"
                : "none",
            }}
            onClick={() => handlePartClick(key)}
          >
            {unlockedParts.includes(key) ? (
              <FavoriteIcon fontSize="inherit" />
            ) : (
              <LockIcon fontSize="inherit" />
            )}
          </IconButton>
        ))}
      </Box>

      {selectedPart && (
        <Card sx={{ maxWidth: "500px", mx: "auto", mt: 4 }}>
          <CardContent sx={{ textAlign: "center", py: 4 }}>
            <Avatar
              sx={{
                bgcolor: "primary.main",
                width: 60,
                height: 60,
                fontSize: "2rem",
                mb: 2,
                mx: "auto",
              }}
            >
              {selectedPart.icon}
            </Avatar>
            <Typography variant="h5" component="h3" gutterBottom>
              {selectedPart.name}
            </Typography>
            <Divider sx={{ my: 2 }} />
            <Typography variant="body1" sx={{ fontStyle: "italic" }}>
              "{selectedPart.message}"
            </Typography>
          </CardContent>
        </Card>
      )}

      <Box sx={{ mt: 6, display: "flex", justifyContent: "center" }}>
        <Button
          variant="outlined"
          size="large"
          startIcon={<CelebrationIcon />}
          href="/"
          sx={{ px: 4 }}
        >
          RecomeÃ§ar
        </Button>
      </Box>
    </Box>
  );
};

export default BodyMap;
