  const [quizStarted, setQuizStarted] = useState(false);
  const [quizCompleted, setQuizCompleted] = useState(false);
  const [unlockedParts, setUnlockedParts] = useState([]);
  const [score, setScore] = useState(0);

  const handleStartQuiz = () => {
    setQuizStarted(true);
  };

  const handleQuizComplete = (finalScore, partsUnlocked) => {
    setScore(finalScore);
    setUnlockedParts(partsUnlocked);
    setQuizCompleted(true);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box
        sx={{
          minHeight: "100vh",
          background: "linear-gradient(135deg, #121212 0%, #1e1e1e 100%)",
          py: 8,
        }}
      >
        <Container maxWidth="md">
          {!quizStarted ? (
            <Box
              sx={{
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                textAlign: "center",
                gap: 4,
              }}
            >
              <Typography variant="h1" component="h1">
                2 Anos de Amor
              </Typography>
              <Typography variant="body1" sx={{ maxWidth: "600px" }}>
                Um presente especial que combina nosso amor com minha paixão por
                código. Complete o quiz para desbloquear memórias especiais em
                cada parte do nosso amor!
              </Typography>

              <Button
                variant="contained"
                size="large"
                startIcon={<CelebrationIcon />}
                onClick={handleStartQuiz}
                sx={{
                  mt: 4,
                  px: 6,
                  py: 2,
                  fontSize: "1.1rem",
                }}
              >
                Começar Nossa Jornada
              </Button>
            </Box>
          ) : !quizCompleted ? (
            <Quiz questions={questions} onComplete={handleQuizComplete} />
          ) : (
            <BodyMap
              bodyParts={bodyParts}
              unlockedParts={unlockedParts}
              score={score}
              totalQuestions={questions.length}
            />
          )}
        </Container>
      </Box>
    </ThemeProvider>
  );
}

export default App;
