import React, { useState } from "react";
import { ThemeProvider, CssBaseline } from "@mui/material";
import { Box, Container, Button, Typography } from "@mui/material";
import CelebrationIcon from "@mui/icons-material/Celebration";
import theme from "./theme";
import Quiz from "./components/Quiz";
import BodyMap from "./components/BodyMap";

const questions = [
  {
    id: 1,
    question: "Onde foi nosso primeiro beijo?",
    options: ["No cinema", "Em casa", "Na cal√ßada"],
    answer: 2,
    partToUnlock: "heart",
  },
  {
    id: 2,
    question: "Qual meu prato preferido que voc√™ faz?",
    options: ["Lasanha", "Strogonoff", "Pizza caseira"],
    answer: 1,
    partToUnlock: "hands",
  },
  {
    id: 3,
    question: "Qual foi nosso primeiro date?",
    options: ["Jantar", "Cinema", "Passeio no parque"],
    answer: 0,
    partToUnlock: "eyes",
  },
];

const bodyParts = {
  heart: {
    name: "Cora√ß√£o",
    message: "Nosso amor bombeia forte desde o primeiro dia! ‚ù§Ô∏è",
    position: { top: "25%", left: "42%" },
    icon: "‚ù§Ô∏è",
  },
  hands: {
    name: "M√£os",
    message:
      "Sempre que nossas m√£os se encontram, eu lembro como √© bom estar com voc√™ ‚úã",
    position: { top: "55%", left: "30%" },
    icon: "‚úã",
  },
  eyes: {
    name: "Olhos",
    message:
      "Seus olhos s√£o meus c√≥digos favoritos - profundos e cheios de significado üëÄ",
    position: { top: "15%", left: "42%" },
    icon: "üëÄ",
  },
};

function App() {
  const [quizStarted, setQuizStarted] = useState(false);
  const [quizCompleted, setQuizCompleted] = useState(false);
  const [unlockedParts, setUnlockedParts] = useState([]);
  const [score, setScore] = useState(0);

  const handleStartQuiz = () => {
    setQuizStarted(true);
  };

  const handleQuizComplete = (finalScore, partsUnlocked) => {
    setScore(finalScore);
    setUnlockedParts(partsUnlocked);
    setQuizCompleted(true);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box
        sx={{
          minHeight: "100vh",
          background: "linear-gradient(135deg, #121212 0%, #1e1e1e 100%)",
          py: 8,
        }}
      >
        <Container maxWidth="md">
          {!quizStarted ? (
            <Box
              sx={{
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                textAlign: "center",
                gap: 4,
              }}
            >
              <Typography variant="h1" component="h1">
                2 Anos de Amor
              </Typography>
              <Typography variant="body1" sx={{ maxWidth: "600px" }}>
                Um presente especial que combina nosso amor com minha paix√£o por
                c√≥digo. Complete o quiz para desbloquear mem√≥rias especiais em
                cada parte do nosso amor!
              </Typography>

              <Button
                variant="contained"
                size="large"
                startIcon={<CelebrationIcon />}
                onClick={handleStartQuiz}
                sx={{
                  mt: 4,
                  px: 6,
                  py: 2,
                  fontSize: "1.1rem",
                }}
              >
                Come√ßar Nossa Jornada
              </Button>
            </Box>
          ) : !quizCompleted ? (
            <Quiz questions={questions} onComplete={handleQuizComplete} />
          ) : (
            <BodyMap
              bodyParts={bodyParts}
              unlockedParts={unlockedParts}
              score={score}
              totalQuestions={questions.length}
            />
          )}
        </Container>
      </Box>
    </ThemeProvider>
  );
}

export default App;
