import React, { useState } from "react";
import {
  Card,
  CardContent,
  Typography,
  Button,
  Box,
  LinearProgress,
  IconButton,
} from "@mui/material";
import FavoriteIcon from "@mui/icons-material/Favorite";
import ArrowForwardIcon from "@mui/icons-material/ArrowForward";

const Quiz = ({ questions, onComplete }) => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [unlockedParts, setUnlockedParts] = useState([]);
  const [selectedOption, setSelectedOption] = useState(null);
  const [isCorrect, setIsCorrect] = useState(null);

  const currentQuestion = questions[currentQuestionIndex];

  const handleAnswer = (optionIndex) => {
    setSelectedOption(optionIndex);
    const correct = optionIndex === currentQuestion.answer;
    setIsCorrect(correct);

    if (correct) {
      setScore(score + 1);
      setUnlockedParts([...unlockedParts, currentQuestion.partToUnlock]);
    }
  };

  const handleNextQuestion = () => {
    setSelectedOption(null);
    setIsCorrect(null);

    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      onComplete(score, unlockedParts);
    }
  };

  return (
    <Box sx={{ maxWidth: "800px", mx: "auto" }}>
      <LinearProgress
        variant="determinate"
        value={((currentQuestionIndex + 1) / questions.length) * 100}
        sx={{
          height: 8,
          borderRadius: 4,
          mb: 4,
          background: "rgba(255, 255, 255, 0.1)",
          "& .MuiLinearProgress-bar": {
            background: "linear-gradient(45deg, #8a2be2, #b388ff)",
          },
        }}
      />

      <Card>
        <CardContent sx={{ py: 4, px: 4 }}>
          <Typography variant="h5" component="h2" gutterBottom sx={{ mb: 4 }}>
            {currentQuestion.question}
          </Typography>

          <Box
            sx={{
              display: "grid",
              gridTemplateColumns: { xs: "1fr", sm: "1fr 1fr" },
              gap: 2,
              mt: 2,
            }}
          >
            {currentQuestion.options.map((option, index) => (
              <Button
                key={index}
                variant={selectedOption === index ? "contained" : "outlined"}
                color={
                  selectedOption === index
                    ? isCorrect
                      ? "primary"
                      : "error"
                    : "inherit"
                }
                onClick={() => !selectedOption && handleAnswer(index)}
                disabled={selectedOption !== null && selectedOption !== index}
                sx={{
                  py: 2,
                  fontSize: "1rem",
                  justifyContent: "flex-start",
                  textAlign: "left",
                }}
              >
                {option}
              </Button>
            ))}
          </Box>

          {selectedOption !== null && (
            <Box
              sx={{
                mt: 4,
                display: "flex",
                alignItems: "center",
                justifyContent: "space-between",
              }}
            >
              <Typography
                variant="h6"
                color={isCorrect ? "primary.main" : "error.main"}
                sx={{ display: "flex", alignItems: "center", gap: 1 }}
              >
                {isCorrect ? (
                  <>
                    <FavoriteIcon /> Correto!
                  </>
                ) : (
                  <>
                    Resposta certa:{" "}
                    {currentQuestion.options[currentQuestion.answer]}
                  </>
                )}
              </Typography>

              <Button
                variant="contained"
                endIcon={<ArrowForwardIcon />}
                onClick={handleNextQuestion}
                sx={{ ml: "auto" }}
              >
                {currentQuestionIndex < questions.length - 1
                  ? "Próxima"
                  : "Ver Resultado"}
              </Button>
            </Box>
          )}
        </CardContent>
      </Card>

      <Typography variant="body2" sx={{ mt: 2, textAlign: "center" }}>
        Pergunta {currentQuestionIndex + 1} de {questions.length}
      </Typography>
    </Box>
  );
};

export default Quiz;
