// src/components/MusicPlayer.js
import React, { useState } from "react";
import { IconButton, Slider, Box } from "@mui/material";
import { PlayArrow, Pause, VolumeUp } from "@mui/icons-material";
import AudioPlayer from "react-audio-player";

export default function MusicPlayer() {
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(0.7);

  return (
    <Box
      sx={{
        position: "fixed",
        bottom: 16,
        right: 16,
        bgcolor: "rgba(0,0,0,0.7)",
        borderRadius: "50px",
        p: 1,
        display: "flex",
        alignItems: "center",
        zIndex: 9999,
      }}
    >
      <IconButton onClick={() => setIsPlaying(!isPlaying)} color="primary">
        {isPlaying ? <Pause /> : <PlayArrow />}
      </IconButton>

      <AudioPlayer
        src="public/musica/Cinnamon-Girl.mp3" // Caminho da sua música
        autoPlay={false}
        loop
        volume={volume}
        controls={false}
        style={{ display: "none" }}
        ref={(element) => {
          if (element) {
            isPlaying
              ? element.audioEl.current.play()
              : element.audioEl.current.pause();
          }
        }}
      />

      <VolumeUp sx={{ ml: 1, color: "primary.main" }} />
      <Slider
        value={volume}
        onChange={(e, newValue) => setVolume(newValue)}
        min={0}
        max={1}
        step={0.1}
        sx={{ width: "100px", ml: 1, color: "primary.main" }}
      />
    </Box>
  );
}
